export default function (...params) {
    const API = params[0];

    let data = "?";

    if (params[0]) {
        for (let key in params[0]) {
            data = `${data}${key}=${params[0][key]}&`;
        }
    }

    //console.log(`${API}${data}`);
    return fetch(`${API}${data}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Access-Control-Allow-Origin": "*"
        }
    });
}
